import React, {useEffect, useState} from'react';
import './App.css';

function App() {
  this.state = {
    books:[]
  }
  const [data, setData] = useState(null);
  const [fetchData, setFetch] = useState(false);

  useEffect(() => {
      if (fetchData) {
         fetch('https://jsonplaceholder.typicode.com/todos')
         .then((response) => response.json())
         .then((data) => setData({books: data}));
      }
   }, [fetchData]);

  return (
    <div className="App">
      <h1>Trip Advisor API</h1>
      <ul>
        <li>Create Site</li>
        <li>Connect to API</li>
      </ul>
      <button onClick={() => setFetch(true)}>Fetch Data</button>
      <div>Boo</div>
      {/* <ul>
        {data.map((book) => <li key={book.id}>{book.title}</li>)}
      </ul> */}
      dd: {this.books.title}
    </div>
  );
}

export default App;
